/**
 * Randomize array element order *in-place/to other array*.
 * Using Fisher-Yates shuffle algorithm.
 */

module.exports = function arrayShuffle(arr, copy) {

  var random;
  var temp;
  var newArr = copy ? arr.slice() : arr;

  for (var i = arr.length - 1; i > 0; i--) {
    random = Math.floor(Math.random() * (i + 1));
    temp = newArr[i];
    newArr[i] = newArr[random];
    newArr[random] = temp;
  }

  return newArr;

};
