var numberIsNan = require('./number/numberIsNan');

var units = ['B', 'kB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];

function byteSizeFormat(num, base) {

  var exponent;
  var unit;
  var neg;

  if (typeof num !== 'number' || numberIsNan(num)) {
    throw new TypeError('Expected a number');
  }

  neg = num < 0;

  if (neg) {
    num = -num;
  }

  if (num < 1) {
    return (neg ? '-' : '') + num + ' B';
  }

  base = base || 1024;

  exponent = Math.min(Math.floor(Math.log(num) / Math.log(base)),
    units.length - 1);
  num = (num / Math.pow(base, exponent)).toFixed(2) * 1;
  unit = units[exponent];

  return (neg ? '-' : '') + num + ' ' + unit;

}

module.exports = byteSizeFormat;
