// clone with flag modification

module.exports = function cloneRegExp(oldRegExp, addedFlags) {
  var flags = '';
  addedFlags = addedFlags || '';
  if (oldRegExp.ignoreCase || /i/i.test(addedFlags)) {
    flags += 'i';
  }
  if (oldRegExp.global || /g/i.test(addedFlags)) {
    flags += 'g';
  }
  if (oldRegExp.multiline || /m/i.test(addedFlags)) {
    flags += 'm';
  }
  return new RegExp(oldRegExp.source, flags);
};

