var hexToRgb = require('./hexToRgb');
var lightness = require('./rgbToGray/lightness');
var rgbToHex = require('./rgbToHex');

module.exports = function hexToGray(hex, method) {

  var rgb = hexToRgb(hex);
  var calculatedColor;

  method = method || lightness;

  calculatedColor = method(rgb[0], rgb[1], rgb[2]);

  return rgbToHex(calculatedColor, calculatedColor, calculatedColor);

};
