var expandHex = require('../util/expandHex');
var hexFullToRgb = require('../regExp/hexFullToRgb');

module.exports = function hexToRgb(hex) {

  var result;

  hex = expandHex(hex);

  result = hexFullToRgb.exec(hex);

  return result ? [
    parseInt(result[1], 16),
    parseInt(result[2], 16),
    parseInt(result[3], 16)
  ] : null;

};
