var hslToRgb = require('./hslToRgb');
var lightness = require('./rgbToGray/lightness');
var rgbToHsl = require('./rgbToHsl');

module.exports = function hslToGray(h, s, l, method) {

  var rgb = hslToRgb(h, s, l);
  var calculatedColor;

  method = method || lightness;

  calculatedColor = method(rgb[0], rgb[1], rgb[2]);

  return rgbToHsl(calculatedColor, calculatedColor, calculatedColor);

};
