// new Date('2012-01-10')
// Tue Jan 10 2012 01:00:00 GMT+0100 (Środkowoeuropejski czas stand.)
// new Date('2012/01/10')
// Tue Jan 10 2012 00:00:00 GMT+0100 (Środkowoeuropejski czas stand.)
// new Date('2012-01-10 00:00')
// new Date('2012-01-10 00:00:00')
// Tue Jan 10 2012 00:00:00 GMT+0100 (Środkowoeuropejski czas stand.)

//2012-01-01, 2012.01.01, 2012/01/01, timestamp, date object -> date object

var isDateObject = require('./isDateObject');
var isValidTimeStamp = require('./isValidTimeStamp');

function parseDate(date) {

  if (isDateObject(date)) {

    return date;

  } else if (isValidTimeStamp(date)) {

    return new Date(date);

  } else if (typeof date === 'string' &&
    /^[0-9]{4}([-\.\/][0-9]{2}){2}$/.test(date)) {

    date = date.split(/[-\.\/]/);

    return new Date(date[0], date[1] - 1, date[2]);

  } else {

    return;

  }

}

module.exports = parseDate;
