var uniqueId = require('../uniqueId');

function identify(domElement, useUniqueId) {
  if (!domElement.id) {
    domElement.id = identify.prefix + identify.autoIdFunction(useUniqueId);
  }
  return domElement.id;
}

identify.autoId = 0;
identify.autoIdFunction = function (useUniqueId) {
  return useUniqueId ? uniqueId() : identify.autoId++;
};
identify.prefix = 'id-auto-';

module.exports = identify;
