var returnMethod = require('./returnMethod');

module.exports = function returnCommonMethods(el) {
  return {
    byClass: returnMethod(el, 'getElementsByClassName'),
    byName: returnMethod(el, 'getElementsByName'),
    byTag: returnMethod(el, 'getElementsByTagName'),
    id: returnMethod(el, 'getElementById'),
    qs: returnMethod(el, 'querySelector'),
    qsa: returnMethod(el, 'querySelectorAll')
  };
};
