/*
 function(args) {
 fn.apply(this, args);
 }.bind(this, arguments)

 is

 Function.prototype.apply.bind(fn, this, arguments);
 */

module.exports = function debounce(fn, delay) {
  var timer = null;
  return function () {
    clearTimeout(timer);
    timer =
      setTimeout(Function.prototype.apply.bind(fn, this, arguments), delay);
  };
};
