function prfnw(name, options) {

  var calc;

  options = options || {};

  if (options.forceDelete) {
    delete prfnw.names[name];
  }

  if (!prfnw.names[name]) {

    prfnw.names[name] = performance.now();

  } else {

    calc = performance.now() - prfnw.names[name];

    if (!options.keep) {
      delete prfnw.names[name];
    }

    if (!options.raw) {
      if (options.log && typeof options.log === 'function') {
        options.log(name, calc);
      } else {
        console.log(name, calc);
      }
    } else {
      return calc;
    }

  }

}

prfnw.names = {};

module.exports = prfnw;
