let names = {};

function prfnw(name, options) {

  let calc;

  options = options || {};

  if (options.forceDelete) {
    delete names[name];
  }

  if (!names[name]) {

    names[name] = performance.now();

  } else {

    calc = performance.now() - names[name];

    if (!options.keep) {
      delete names[name];
    }

    if (!options.raw) {
      if (options.log && typeof options.log === 'function') {
        options.log(name, calc);
      } else {
        console.log(name, calc);
      }
    } else {
      return calc;
    }

  }

}

export default prfnw;

export { names };
