var isNumber = require('./is');

module.exports = function numberInRange(num, min, max) {

  var minMax;
  var returnValue = false;

  if (isNumber(num) && isNumber(min) && isNumber(max)) {
    minMax = [min, max].sort();
    returnValue = num >= minMax[0] && num <= minMax[1];
  }

  return returnValue;

};
