// a - b
// c - x
// x = (b * c) / a

// skipCheck for speed

var isNumber = require('./is');

module.exports = function numberRatio(a, b, c, skipCheck) {
  if (!skipCheck && (!isNumber(a) || !isNumber(b) || !isNumber(c))) {
    throw new Error('One of arguments is not a number!');
  }
  return (b * c) / a;
};
