// http://jsperf.com/arabic-to-roman/6

var isNumber = require('./is');

var dictionary = [
  ['', 'M', 'MM', 'MMM'],
  ['', 'C', 'CC', 'CCC', 'CD', 'D', 'DC', 'DCC', 'DCCC', 'CM'],
  ['', 'X', 'XX', 'XXX', 'XL', 'L', 'LX', 'LXX', 'LXXX', 'XC'],
  ['', 'I', 'II', 'III', 'IV', 'V', 'VI', 'VII', 'VIII', 'IX']
];

function convertToRoman(num) {
  if (!isNumber(num) || num >= 4000 || num <= 0) {
    return;
  }
  return ('000' + num).slice(-4).split('').reduce(function (str, strNext, i) {
    return str += convertToRoman.dictionary[i][strNext];
  }, '');
}

module.exports = convertToRoman;
module.exports.dictionary = dictionary;
