// properties only
// 2.5x smaller than deep

var ownProperty = require('../ownProperty');

module.exports = function objectExtendLite() {

  var args = arguments;
  var c = args.length;
  var ext = args[0];
  var cur;

  if (c < 2) {
    return ext;
  }

  for (var i = 1; i < c; i++) {
    cur = args[i];
    for (var j in cur) {
      if (ownProperty.call(cur, j)) {
        ext[j] = typeof cur[j] === 'object'
          ? objectExtendLite(ext[j], cur[j]) : cur[j];
      }
    }
  }

  return ext;

};
