// hashFnv32a

// asString = Convert to 8 digit hex string

module.exports = function hash(str, asString) {
  var hval = 0x811c9dc5;
  if (!str) {
    return;
  }
  str += '';
  for (var i = 0, c = str.length; i < c; i++) {
    hval ^= str.charCodeAt(i);
    hval +=
      (hval << 1) + (hval << 4) + (hval << 7) + (hval << 8) + (hval << 24);
  }
  return asString
    ? ('0000000' + (hval >>> 0).toString(16)).substr(-8) : hval >>> 0;
};
