var replace = require('./replaceRegExp');

function utfToIso(str) {
  return replace(str, utfToIso.regExp, utfToIso.charObj);
}
function isoToUtf(str) {
  return replace(str, isoToUtf.regExp, isoToUtf.charObj);
}

utfToIso.regExp = /[ąśź]/ig;
isoToUtf.regExp = /[±ˇ¶¦Ľ¬]/g;
utfToIso.charObj = {
  'ą': '±',
  'Ą': 'ˇ',
  'ś': '¶',
  'Ś': '¦',
  'ź': 'Ľ',
  'Ź': '¬'
};
isoToUtf.charObj = {
  '±': 'ą',
  'ˇ': 'Ą',
  '¶': 'ś',
  '¦': 'Ś',
  'Ľ': 'ź',
  '¬': 'Ź'
};

module.exports = {
  utfToIso: utfToIso,
  isoToUtf: isoToUtf
};
