// regexp quicker in changing parts of string than loop

// no support for &oacute; -> o

var replace = require('./replaceRegExp');

function unPolish(str) {
  return replace(str, unPolish.regExp, unPolish.charObj);
}

unPolish.regExp = /[ąćęłńóśźż]/ig;
unPolish.charObj = {
  'ą': 'a',
  'ć': 'c',
  'ę': 'e',
  'ł': 'l',
  'ń': 'n',
  'ó': 'o',
  'ś': 's',
  'ź': 'z',
  'ż': 'z',
  'Ą': 'A',
  'Ć': 'C',
  'Ę': 'E',
  'Ł': 'L',
  'Ń': 'N',
  'Ó': 'O',
  'Ś': 'S',
  'Ź': 'Z',
  'Ż': 'Z'
};

module.exports = unPolish;
