// key-value pair replacement

module.exports = function replaceLoop(str, charObj) {

  var returnChar;

  if (!str || !charObj) {
    return str;
  }

  str = str.split('');

  for (var i = 0, c = str.length; i < c; i++) {
    returnChar = charObj[str[i]];
    if (returnChar) {
      str[i] = returnChar;
    }
  }

  str = str.join('');

  return str;

};
