module.exports = function toEntity(str) {

  var charAt;
  var returnString = '';

  if (!str || typeof str !== 'string') {
    throw new Error('No string!');
  }

  for (var i = 0, c = str.length; i < c; i++) {
    charAt = str.charCodeAt(i);
    returnString += charAt === 10 ? '\n' : '&#' + charAt + ';';
  }

  return returnString;

};
