// url - eg. location href
// str - 'url-encoded-data'

module.exports = function urlAddData(url, str) {

  var undef;
  var index;
  var hash = '';
  var sign;

  if (!url) {

    return undef;

  } else if (!str) {

    return url;

  } else {

    index = url.indexOf('#');

    if (index > -1) {
      hash = url.substring(index);
      url = url.substring(0, index);
    }

    sign = url.indexOf('?') < 0 ? '?' : '&';

    return url + sign + str + hash;

  }

};
